var fontColor = "#000";
var seriesName = "";
let noramlSize = 16;
var datas = {
    dataArr1: [20],
    dataArr2: [130],
    dataArr3: [150]
};
var total = datas.dataArr1[0] + datas.dataArr2[0] + datas.dataArr3[0];
var pencent1 = ((datas.dataArr1[0] / total) * 100).toFixed(2);
var pencent2 = ((datas.dataArr2[0] / total) * 100).toFixed(2);
var pencent3 = ((datas.dataArr3[0] / total) * 100).toFixed(2);
var symbolSize = [40, 80];
option = {
    animation: false,
    xAxis: {
        show: false,
        splitLine: {
            show: false
        }
    },
    yAxis: {
        show: false,
        data: [],
        axisTick: {
            show: false
        },
        axisLine: {
            show: false
        }
    },
    tooltip: {
        show: true,
        borderRadius: 5,
        backgroundColor: "#f6f6f6",
        textStyle: {
            color: "#000",
            fontSize: 20
        },
        formatter: function(v) {
            if (v.seriesName == "绿色") {
                return (
                    '<div class="tooltipBox">' +
                    '<img src="data:image/png;base64," alt />' +
                    '  <span style="font-size:18px">' +
                    v.seriesName +
                    " : </span>" +
                    '<span style="font-size:25px">' +
                    v.value +
                    "</span>" +
                    '<span style="font-size:16px"> 个</span><br/>' +
                    '<span style="font-size:16px">占比 : ' +
                    pencent1 +
                    " %</span>" +
                    "</div>"
                );
            } else if (v.seriesName == "紫色") {
                return (
                    '<div class="tooltipBox">' +
                    '<img src="data:image/png;base64," alt />' +
                    '  <span style="font-size:18px">' +
                    v.seriesName +
                    " : </span>" +
                    '<span style="font-size:25px">' +
                    datas.dataArr2 +
                    "</span>" +
                    '<span style="font-size:16px"> 个</span><br/>' +
                    '<span style="font-size:16px">占比 : ' +
                    pencent2 +
                    " %</span>" +
                    "</div>"
                );
            } else if (v.seriesName == "蓝色") {
                return (
                    '<div class="tooltipBox">' +
                    '<img src="data:image/png;base64," alt />' +
                    '  <span style="font-size:18px">' +
                    v.seriesName +
                    " : </span>" +
                    '<span style="font-size:25px">' +
                    datas.dataArr3 +
                    "</span>" +
                    '<span style="font-size:16px"> 个</span><br/>' +
                    '<span style="font-size:16px">占比 : ' +
                    pencent3 +
                    " %</span>" +
                    "</div>"
                );
            }
        }
    },
    series: [{
            type: "pictorialBar",
            name: "绿色",
            barCategoryGap: 2,
            itemStyle: {
                normal: {
                    color: {
                        type: "linear",
                        x: 0,
                        y: 0,
                        x2: 0,
                        y2: 1,
                        colorStops: [{
                                offset: 0,
                                color: "rgba(53, 195, 194, 1)" // 0% 处的颜色
                            },
                            {
                                offset: 0.5,
                                color: "rgba(223, 213, 252, 0.5)" // 50% 处的颜色
                            },
                            {
                                offset: 1,
                                color: "rgba(53, 195, 194, 1)" // 100% 处的颜色
                            }
                        ],
                        globalCoord: false // 缺省为 false
                    }
                }
            },
            symbol: "roundRect",
            symbolRepeat: "fixed",
            symbolClip: true,
            symbolSize: symbolSize,
            data: datas.dataArr1,
            z: 11
        },
        {
            type: "pictorialBar",
            barCategoryGap: 2,
            name: "紫色",
            itemStyle: {
                normal: {
                    color: {
                        type: "linear",
                        x: 0,
                        y: 0,
                        x2: 0,
                        y2: 1,
                        colorStops: [{
                                offset: 0,
                                color: "rgba(159, 144, 205, 1)" // 0% 处的颜色
                            },
                            {
                                offset: 0.5,
                                color: "rgba(223, 213, 252, 0.5)" // 50% 处的颜色
                            },
                            {
                                offset: 1,
                                color: "rgba(159, 144, 205, 1)" // 100% 处的颜色
                            }
                        ],
                        globalCoord: false // 缺省为 false
                    }
                }
            },
            symbol: "roundRect",
            symbolRepeat: "fixed",
            symbolClip: true,
            symbolSize: symbolSize,
            data: [datas.dataArr1[0] + datas.dataArr2[0]],
            z: 10
        },
        {
            type: "pictorialBar",
            barCategoryGap: 2,
            name: "蓝色",
            itemStyle: {
                normal: {
                    color: {
                        type: "linear",
                        x: 0,
                        y: 0,
                        x2: 0,
                        y2: 1,
                        colorStops: [{
                                offset: 0,
                                color: "rgba(125, 176, 235, 1)" // 0% 处的颜色
                            },
                            {
                                offset: 0.5,
                                color: "rgba(195, 223, 255, 0.5)" // 50% 处的颜色
                            },
                            {
                                offset: 1,
                                color: "rgba(125, 176, 235, 1)" // 100% 处的颜色
                            }
                        ],
                        globalCoord: false // 缺省为 false
                    }
                }
            },
            symbolRepeat: "fixed",
            symbol: "roundRect",
            symbolSize: symbolSize,
            symbolClip: true,
            data: [total],
            z: 5
        }
    ]
}
