mapper.update:
UPDATE st_pptn_r_assist
stcd = #{stcd}, time = #{time}, hour = #{hour}, threehour = #{threehour}, day = #{day}, type = #{type}
WHERE id = #{id}

mapper.select:
SELECT assist.hour data,assist.time,a.area_code,c.adnm area_name,assist.stcd,a.st_name stnm FROM `st_pptn_r_assist` assist
    LEFT JOIN att_st_base a ON a.st_code = assist.stcd LEFT JOIN att_adcd_base c ON a.area_code = c.adcd WHERE assist.`hour` > 30

    AND a.area_code = #{areaCode}


    AND assist.time >= #{startTime}


    AND assist.time <= #{endTime}


    AND a.st_name LIKE CONCAT('%',#{stationName},'%')


    AND a.bas = #{bas}

SELECT assist.threehour data,assist.time,a.area_code,c.adnm area_name,assist.stcd,a.st_name stnm FROM `st_pptn_r_assist` assist
    LEFT JOIN att_st_base a ON a.st_code = assist.stcd LEFT JOIN att_adcd_base c ON a.area_code = c.adcd
WHERE assist.`threehour` > 60

    AND assist.time >= #{startTime}


    AND assist.time <= #{endTime}


    AND a.area_code = #{areaCode}


    AND a.st_name LIKE CONCAT('%',#{stationName},'%')


    AND a.bas = #{bas}

SELECT assist.day data,assist.time,a.area_code,c.adnm area_name,assist.stcd,a.st_name stnm FROM `st_pptn_r_assist` assist
    LEFT JOIN att_st_base a ON a.st_code = assist.stcd LEFT JOIN att_adcd_base c ON a.area_code = c.adcd
WHERE assist.`day` > 100 AND type = 2

    AND assist.time >= #{startTime}


    AND assist.time <= #{endTime}


    AND a.area_code = #{areaCode}


    AND a.st_name LIKE CONCAT('%',#{stationName},'%')


    AND a.bas = #{bas}

SELECT assist.day data,DATE_FORMAT(assist.time,'%Y-%m-%d') time,a.area_code,c.adnm area_name,assist.stcd,a.st_name stnm FROM `st_pptn_r_assist` assist
LEFT JOIN att_st_base a ON a.st_code = assist.stcd LEFT JOIN att_adcd_base c ON a.area_code = c.adcd WHERE type = 2

    AND assist.time >= #{startTime}


    AND assist.time <= #{endTime}


    AND a.area_code = #{areaCode}


    AND a.st_name LIKE CONCAT('%',#{stationName},'%')

order by assist.time

SELECT CONCAT(DATE_FORMAT(b.time,'%Y-%m-%d %H'),'时-',DATE_FORMAT(DATE_ADD(b.time,INTERVAL +1 HOUR),'%H'),'时') tm, b.hour data, asb.st_name stnm,asb.st_code stCode FROM ( SELECT k.stcd, MAX( k.hour ) hour FROM st_pptn_r_assist k GROUP BY stcd ) a LEFT JOIN st_pptn_r_assist b ON ( a.stcd = b.stcd AND a.hour = b.hour ) LEFT JOIN att_st_base asb ON asb.st_code = a.stcd where 1=1
AND asb.area_code = #{areaCode}
AND b.time >= #{startTime}
AND b.time <= #{endTime}
AND asb.st_name LIKE CONCAT('%',#{stationName},'%')
GROUP BY a.stcd ORDER BY a.stcd

SELECT CONCAT(DATE_FORMAT(b.time,'%Y-%m-%d %H'),'时-',DATE_FORMAT(DATE_ADD(b.time,INTERVAL +3 HOUR),'%H'),'时') tm, b.threehour data, asb.st_name stnm,asb.st_code stCode FROM ( SELECT stcd, MAX( threehour ) threehour FROM st_pptn_r_assist GROUP BY stcd ) a LEFT JOIN st_pptn_r_assist b ON ( a.stcd = b.stcd AND a.threehour = b.threehour ) LEFT JOIN att_st_base asb ON asb.st_code = a.stcd where 1=1
AND asb.area_code = #{areaCode}
AND b.time >= #{startTime}
AND b.time <= #{endTime}
AND asb.st_name LIKE CONCAT('%',#{stationName},'%')
GROUP BY a.stcd ORDER BY a.stcd
